            new User { Id = Guid.Parse("12345678-0000-0000-0001-123456780002"), Name = "Kamuf Larsen", Alias = "Kamuflasjen" ,Email = "kamuflasjen@epost.no", PasswordHash = Authentication.HashPassword("Hopla2025!"), Admin = true, Premium = true, VerifiedTrail = true, ProfilePictureUrl = "https://plus.unsplash.com/premium_photo-1661868397660-8c52f33c5934", Description = "Har utmerket meg spesielt i kunsten å balansere en pinnsvin på hodet mens jeg hopper på trampoline. Er den eneste i historien som har vunnet en sjakkturnering ved å blunke strategisk til motstanderne. Har en medfødt evne til å forstå hva lamaer prøver å si, og kan navigere i ukjente byer ved å lukte seg frem til nærmeste pannekakebod. En gang syklet jeg over en innsjø – ingen vet helt hvordan, men teoriene involverer både helium og viljestyrke." },
            new User { Id = Guid.Parse("12345678-0000-0000-0001-123456780003"), Name = "Emba Larsen", Alias = "Embalasjen" ,Email = "embalasjen@epost.no", PasswordHash = Authentication.HashPassword("Hopla2025!"), Admin = true, Premium = true, VerifiedTrail = true, ProfilePictureUrl = "https://images.unsplash.com/photo-1580489944761-15a19d654956", Description = "Jeg har en imponerende evne til å starte et prosjekt, bli distrahert av en sommerfugl, og plutselig finne meg selv i en helt annen by med en ny hobby. En gang lærte jeg meg å spille munnspill mens jeg sto på en enhjulssykkel – ufrivillig. Jeg er kjent for mine diplomatiske ferdigheter med duer og har en teori om at jeg en gang var en sjørøver i et tidligere liv. Kan lage pannekaker uten å bruke hender (ikke spør hvordan, det er en lang historie)" },
            new User { Id = Guid.Parse("12345678-0000-0000-0001-123456780004"), Name = "Thea Dyring", Alias = "JockeyThea", Email = "jockeythea@epost.no", PasswordHash = Authentication.HashPassword("Hopla2025!"), Admin = true, Premium = true, VerifiedTrail = true, ProfilePictureUrl = "https://images.unsplash.com/photo-1544005313-94ddf0286df2", Description = "Jeg kan fikse alt – så lenge det involverer å slå det lett på siden og si ‘prøv igjen’. Har en naturlig evne til å skrive kode som kun jeg forstår, og jeg mener fortsatt at min første oppfinnelse, ‘den selvstirrende suppeskjeen’, var forut for sin tid. Jeg har en tendens til å snakke med roboter, og en av dem svarte meg en gang. Det var både et gjennombrudd og starten på et lite eksistensielt sammenbrudd" },
            new User { Id = Guid.Parse("12345678-0000-0000-0001-123456780005"), Name = "Ann Iren Haakenstad", Alias = "JockeyAnnIren", Email = "aih@hopla.no", PasswordHash = Authentication.HashPassword("Hopla2025!"), Admin = true, Premium = true, VerifiedTrail = true, ProfilePictureUrl = "https://images.unsplash.com/photo-1554151228-14d9def656e4", Description = "Jeg er den personen som alltid finner en ‘kreativ’ løsning, som ofte fører til at noen må ringe brannvesenet. Har en lang liste over ting jeg har prøvd å bygge, inkludert en hjemme-laget katapult (det gikk ikke bra) og en automatisk pizza-kaster (den gikk altfor bra). Tror at jeg en dag kommer til å oppdage en ny fysisk lov ved et uhell. Har også verdensrekord i å miste nøkler på de minst logiske stedene." },
            new User { Id = Guid.Parse("12345678-0000-0000-0001-123456780006"), Name = "Anna Louise Sedolfsen", Alias = "JockeyAnnaLouise", Email = "als@hopla.no", PasswordHash = Authentication.HashPassword("Hopla2025!"), Admin = true, Premium = true, VerifiedTrail = true, ProfilePictureUrl = "https://images.unsplash.com/photo-1529626455594-4ff0802cfb7e", Description = "Jeg er fascinert av mysterier – som hvorfor sokker forsvinner i vaskemaskinen, eller hvordan jeg alltid vet sangtekstene til sanger jeg aldri har hørt før. Har et sjette sans for å finne kaker på kontoret og kan lukte gratis mat på flere kilometers avstand. En gang prøvde jeg å lære en papegøye å si ‘ikke stol på ham’, bare for å se hvordan folk reagerte. Planen min for fremtiden? Fortsette å improvisere og late som jeg har kontroll." },
            new User { Id = Guid.Parse("12345678-0000-0000-0001-123456780007"), Name = "Helle Kopter", Alias = "Rotorsnurror", Email = "thea@hopla.no", PasswordHash = Authentication.HashPassword("Hopla2025!"), ProfilePictureUrl = "https://images.unsplash.com/photo-1489424731084-a5d8b219a5bb", Description = "Jeg har en tendens til å gå inn i rom og umiddelbart glemme hvorfor jeg er der, men jeg ser alltid på det som en mulighet til å oppdage noe nytt. Jeg er kjent for min uovertrufne evne til å snuble over flate gulv og forvandle en enkel oppgave til en episk reise. En gang prøvde jeg å lage en enkel omelett og endte opp med en ny oppfinnelse – ‘flammekasteren som en gang var en stekepanne’. Jeg mener fortsatt at det var verdt det." },
            new User { Id = Guid.Parse("12345678-0000-0000-0001-123456780008"), Name = "Heli Kopeter", Alias = "HeliKoPeter", Email = "thea@hopla.no", PasswordHash = Authentication.HashPassword("Hopla2025!"), ProfilePictureUrl = "https://images.unsplash.com/photo-1509460913899-515f1df34fea", Description = "Jeg har en sterk mistanke om at jeg egentlig kommer fra fremtiden, men at tidsmaskinen min gikk tom for batteri. Forklarer i hvert fall hvorfor jeg alltid vet hva som skjer før det skjer (eller kanskje jeg bare er veldig god til å gjette). Jeg bruker mesteparten av dagen på å lete etter briller jeg allerede har på meg, og resten av tiden på å prøve å overbevise folk om at jeg faktisk vet hva jeg driver med. Hint: Det gjør jeg ikke." },
            new User { Id = Guid.Parse("12345678-0000-0000-0001-123456780009"), Name = "Dag Jesper Lang", Alias = "JesperDagenLang", Email = "thea@hopla.no", PasswordHash = Authentication.HashPassword("Hopla2025!"), ProfilePictureUrl = "https://images.unsplash.com/photo-1568038479111-87bf80659645", Description = "Jeg har en fascinerende evne til å snakke lenge om ting jeg egentlig ikke forstår. En gang forklarte jeg kvantefysikk for en gjeng måker – de var ikke imponert. Jeg mener fortsatt at jeg burde fått en æresdoktorgrad i ‘usannsynlige livsvalg’ og ‘avansert prokrastinering’. På CV-en min står det at jeg er en ‘problemløser’, men det gjelder hovedsakelig problemer jeg selv har skapt." },
            new User { Id = Guid.Parse("12345678-0000-0000-0001-123456780010"), Name = "Per Brudløs", Alias = "SinglePer", Email = "thea@hopla.no", PasswordHash = Authentication.HashPassword("Hopla2025!"), ProfilePictureUrl = "https://images.unsplash.com/photo-1514626585111-9aa86183ac98", Description = "Jeg kjøpte en gang en trampoline klokken to om natten fordi det virket som en god idé. Det var det ikke. Har en urovekkende tendens til å si ‘hvor vanskelig kan det være?’ rett før ting eksploderer eller går fullstendig galt. Min stolteste prestasjon er å ha fullført en tredagers fjelltur uten å pakke med sokker – det var en læringsopplevelse jeg helst vil glemme. Hvis du trenger noen til å improvisere en løsning på et problem, er jeg personen. Hvis du vil at løsningen faktisk skal fungere… kanskje spør noen andre." },
            new User { Id = Guid.Parse("12345678-0000-0000-0001-123456780011"), Name = "Magne Jernilden", Alias = "MangeJernIIlden", Email = "thea@hopla.no", PasswordHash = Authentication.HashPassword("Hopla2025!"), ProfilePictureUrl = "https://images.unsplash.com/photo-1557053910-d9eadeed1c58", Description = "Jeg har en mistanke om at jeg er en superhelt, men at ingen har fortalt meg hva kreftene mine er ennå. Kanskje det er min evne til å alltid velge den tregeste kassakøen, eller min superkraft til å søle kaffe på meg selv i de mest kritiske øyeblikkene. En gang prøvde jeg å snike meg usett ut av et rom – klarte istedenfor å rive ned et bord og vekke hele bygningen. Stealth er ikke min sterke side." },
